import { useState } from "react";
import { useLogin } from "../hooks/useLogin";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { login, error, isLoading } = useLogin();

  const handleSubmit = async (e) => {
    e.preventDefault();

    await login(email, password);
  };

  return (
    <div>
      <div className="main">
        <h1>Welcome Coders!</h1>
        <p>
          BetterCodr is an online platform where you can feel comfortable asking
          for help. Better coder offers professional code reviews for
          developersa who are still in the process of perfection their craft!
        </p>
        <h3>Why BetterCodr?</h3>
        <p>
          On BetterCodr, code reviwers have an incentive to help others.
          Reviewing someone else's code can be a tedious and time consuming
          process, and for that reason many people don't have the extra time to
          help others out. On BetterCodr, code reviews gain an incentive for
          helping out their peers. For each review that user makes, that user
          will recieve 25 Codr coins!
        </p>
      </div>
      <form className="login" onSubmit={handleSubmit}>
        <h3>Log in</h3>
        <label>Email</label>
        <input
          type="email"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
        />
        <label>Password</label>
        <input
          type="password"
          onChange={(e) => setPassword(e.target.value)}
          value={password}
        />
        <button disabled={isLoading}>Log in</button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default Login;
